return (() => {
  try {
    // Obt√©m os dados
    let dados = $json.data;

    // Se os dados n√£o forem um array, tente normalizar
    if (!Array.isArray(dados)) {
      if (dados && typeof dados === 'object') {
        // Se for um objeto √∫nico, coloca dentro de um array
        dados = [dados];
      } else {
        // Caso contr√°rio, retorna um erro
        return [{ mensagem: "Erro: Os dados fornecidos n√£o s√£o v√°lidos para processamento." }];
      }
    }

    // Processa os dados agora normalizados
    const resumo = dados.map(item => {
      // Valida√ß√µes para campos individuais
      const nomeValido = typeof item.Nome === 'string' ? item.Nome.trim() : null;
      const isNomeInvalido = !nomeValido || nomeValido === "?" || nomeValido.toLowerCase() === "desconhecido";
      const nomeUsuario = isNomeInvalido ? "Usu√°rio n√£o identificado" : nomeValido;

      const mensagemRespondida = item["Mensagem respondida"]
        ? `Mensagem Respondida: ${item["Mensagem respondida"]}`
        : "Mensagem Respondida: Nenhuma";

      return `Nome: ${nomeUsuario}, Mensagem: ${item.Mensagem || "Mensagem n√£o dispon√≠vel"}, ${mensagemRespondida}, Hor√°rio: ${item.Hor√°rio || "Hor√°rio n√£o informado"}`;
    }).join('\n');

    return [{ mensagem: resumo }];
  } catch (error) {
    return [{ mensagem: `Erro ao processar as mensagens: ${error.message}` }];
  }
})();

